import { Html5Qrcode } from "html5-qrcode";
import React, { useState, useEffect } from "react";
import "./App.css";

function App() {
  const [isEnabled, setEnabled] = useState(false);
  const [qrMessage, setQrMessage] = useState("");
  const [items, setItems] = useState([]);

  useEffect(() => {
    const config = { fps: 10, qrbox: { width: 200, height: 200 } };

    const html5QrCode = new Html5Qrcode("qrCodeContainer");

    const qrScanerStop = () => {
      if (html5QrCode && html5QrCode.isScanning) {
        html5QrCode
          .stop()
          .then((ignore) => console.log("Scaner stop"))
          .catch((err) => console.log("Scaner error"));
      }
    };

    const qrCodeSuccess = async(decodedText) => {
      setEnabled(false);
      setQrMessage(decodedText);
      fetch('https://192.168.0.108:2399/get-user-data', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ uid: decodedText }),
      }).then(response => response.json()).then(data => setItems(data));
    };

    if (isEnabled) {
      html5QrCode.start({ facingMode: "environment" }, config, qrCodeSuccess);
      setQrMessage("");
    } else {
      qrScanerStop();
    }

    return () => {
      qrScanerStop();
    };
  }, [isEnabled]);

  return (
    <div className="scaner">
      <div id="qrCodeContainer" />
      {qrMessage && <div className="qr-message">{items["exists"]}</div>}
      <button className="start-button" onClick={async() => {
      setEnabled(!isEnabled);}}>
        {isEnabled ? "Выключить камеру" : "Включить камеру"}
      </button>
    </div>
  );
}

export default App;
